<?php

namespace Levi9\BatteriesBundle\Entity;

use Doctrine\ORM\EntityRepository;

/**
 * BatteriesRepository
 *
 * This class was generated by the Doctrine ORM. Add your own custom
 * repository methods below.
 */
class BatteriesRepository extends EntityRepository
{
    /**
     * Returns statistics.
     *
     * @return array
     */
    public function getStatistics()
    {
        // todo: if you are working with current entity you can omit it type:
        // return $this->createQueryBuilder('b')
        //     ->select('SUM(b.count) as stat_count, b.type')
        //     ->groupBy('b.type')
        //     ->getQuery()
        //     ->getResult();

        return $this->getEntityManager()
            ->createQueryBuilder()
            ->select('b.type')
            ->addSelect('SUM(b.count) AS stat_count')
            ->from('Levi9BatteriesBundle:Batteries', 'b')
            ->groupBy('b.type')
            ->getQuery()
            ->getResult()
        ;
    }
}
